import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

export default function Question({history, location}) {

    const [result, setResult] = useState(location.state.list.concat());
    let cnt = 0;

    for( let num of location.state.list){

        cnt++;
    }

    function handleClick1() {

        result[cnt] = "1";
        save_result();
    }

    function handleClick2() {

        result[cnt] = "2";
        save_result();
    }

    function handleClick3() {

        result[cnt] = "3";
        save_result();
    }

    function handleClick4() {

        result[cnt] = "4";
        save_result();
    }

    function handleClick5() {

        result[cnt] = "5";
        save_result();
    }

    function handleClick6() {

        result[cnt] = "6";
        save_result();  
    }

    function save_result() {
        let id = "210";
        let pwd1 = "210";
        let pwd2 = "210";

        axios.get('/join_member', { params: { id, pwd1, pwd2 }}).
      then(res => {
        if(res.data.result.cnt){ 


        //   history.push({
        //     pathname: '/'
        //   });

            history.push({
            pathname: '/save_result',
            state: { list: result }
        });

        }

      })
      .catch(res => {console.log("ERR");});


    };

    return (
        <div>
            <div> 관심사 태그를 선택해 주세요!</div>
            <QuestionStyled>
                <button onClick={handleClick1}>#액티비티</button>
                <button onClick={handleClick2}>#쇼핑</button>
                <button onClick={handleClick3}>#맛집</button>
                <button onClick={handleClick4}>#문호/예술</button>
                <button onClick={handleClick5}>#파티</button>
                <button onClick={handleClick6}>#자연</button>
            </QuestionStyled>
        </div>

    )
}

var QuestionStyled = styled.div`

    button {
        width: 100px;
        height: 107px;
        background: #2f3438;
        border-radius: 10px;
        display: flex;
        justify-content: center;
        align-items: center;
        font-size: 20px;
        color: #fff;
        font-weight: bold;
        cursor: pointer;
    }
`;
