                pathname: '/result',
                state: { list: result }
            });
        }
    }

    return (
        <ConsultStyled>
            <Header count={count} total={list.length} />
            <div className="question-container">
                {questionList}
            </div>
            <div className="logo">
                <img src={logo}/>
            </div>
        </ConsultStyled>
    )
};

const ConsultStyled = styled.div`
    max-width: 752px;
    margin: 0 auto;
    width: 100%;
    box-sizing: border-box;
    padding: 0 10px;
    
    .question-container {
