import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

export default function Question({history, location}) {

    const [result, setResult] = useState(location.state.list.concat());
    let cnt = 0;

    for( let num of location.state.list){

        cnt++;
    }

    function handleClick1() {

        result[cnt] = "1";
        save_result();
    }

    function handleClick2() {

        result[cnt] = "2";
        save_result();
    }

    function handleClick3() {

        result[cnt] = "3";
        save_result();
    }

    function handleClick4() {

        result[cnt] = "4";
        save_result();
    }

    function handleClick5() {

        result[cnt] = "5";
        save_result();
    }

    function handleClick6() {

        result[cnt] = "6";
        save_result();  
    }

    function save_result() {

        let q1 = 1;
        let q2 = 1;
        let q3 = 1;
        let q4 = 1;
        let q5 = 1;
        let q6 = 1;
        let q7 = 1;
        let q8 = 1;
        let q9 = 1;
        let q10 = 1;
        let q11 = 1;
        let q12 = 1;
        let q13 = 1;
        let q14 = 1;
        let q15 = 1;



        axios.get('/save_result', { params: { q1, q2, q3, q4, q5, q6, q7, q8, q9, q10, q11, q12, q13, q14, q15 }}).
      then(res => {

        //   history.push({
        //     pathname: '/'
        //   });
            console.log("in!")
            history.push({
            pathname: '/result',
            state: { list: result }
        });

      })
      .catch(res => {console.log("ERR");});


    };

    return (
        <div>
            <div> 관심사 태그를 선택해 주세요!</div>
            <QuestionStyled>
                <button onClick={handleClick1}>#액티비티</button>
                <button onClick={handleClick2}>#쇼핑</button>
                <button onClick={handleClick3}>#맛집</button>
                <button onClick={handleClick4}>#문호/예술</button>
                <button onClick={handleClick5}>#파티</button>
                <button onClick={handleClick6}>#자연</button>
            </QuestionStyled>
        </div>

    )
}

var QuestionStyled = styled.div`

    button {
        width: 100px;
        height: 107px;
        background: #2f3438;
        border-radius: 10px;
        display: flex;
        justify-content: center;
        align-items: center;
        font-size: 20px;
        color: #fff;
        font-weight: bold;
        cursor: pointer;
    }
`;
